package basic_java_operations;

public class TernaryOperatorOperation {

	public static void main(String[] args) {
		
		int a = 20;
		int b = 10;
		
		int min = (a < b) ? a : b;
		
		int val = (a < b) ? a+b : a-b;
		
		System.out.println(min);
		System.out.println(val);
		// TODO Auto-generated method stub

	}

}
