package pattern_print;

import java.util.Scanner;

public class Pattern14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner scn = new Scanner(System.in);
		int n = scn.nextInt();
		
		
		for(int i = 1 ; i<=10 ; i++) {
			
			System.out.println( n + " * " + i + " = " + n*i);
		}
		
		

	}

}
